mapper.sql:
join ( select distinct prime.data_source, prime.project_id as activity_projects, count(prime.activity_id) over (partition by prime.project_id) project_activity_count, sum(prime.result_count) over (partition by prime.project_id) project_result_count from activity_sum prime
join station_sum quaternary on prime.station_id = quaternary.station_id and prime.data_source_id = quaternary.data_source_id
) tertiary on prime.project_dim_value = tertiary.activity_projects

join ( select distinct prime.data_source, prime.project_id as activity_projects, count(distinct prime.activity_id) over (partition by prime.project_id) project_activity_count, sum(prime.result_count) over (partition by prime.project_id) project_result_count from result_sum prime
join station_sum quaternary on prime.station_id = quaternary.station_id and prime.data_source_id = quaternary.data_source_id
) tertiary on prime.project_dim_value = tertiary.activity_projects
